import React from 'react';
import login from '../logo.png';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      buttonDisabled: true,
      name: '',
      redirect: false,
      loadingMessage: false,
    };

    this.handleInput = this.handleInput.bind(this);
  }

  handleInput({ target: { value } }) {
    // css
    const MIN_LENGTH = 3;
    const teste = value.length >= MIN_LENGTH;

    this.setState({
      name: value,
      buttonDisabled: !teste,
    });
  }

  handleClick() {
    const { name } = this.state;

    this.setState({ loadingMessage: true });
    // Utilizando o .then porque na função foi feito uma new Promise
    createUser({ name }).then(() => {
      this.setState({
        loadingMessage: false,
        redirect: true,
      });
    });
  }
  render() {
    return (
      <div>
        <img id="logo-login" src={ login } alt="logo" />
        <main className="containerLogin">

          <form id="form-login" action="">

            <input
              onChange={ this.handleInput }
              id="input-login"
              data-testid="login-name-input"
              type="text"
              placeholder="Nome"
            />

            <button
              onClick={ () => this.handleClick() }
              disabled={ buttonDisabled }
              id="button-login"
              data-testid="login-submit-button"
              type="button"
            >
              Entrar
            </button>

          </form>

        </main>
      </div>
    );
  }
}

export default Login;
