import React from 'react';
import Header from '../components/Header';
import albumAPI from '../services/albumAPI';

class Search extends React.Component {
  constructor() {
    super();

    this.state = {
      loading: false,
      hidden: false,
      value: '',
      disabled: true,
    };
  }

  handleInput = ({ target }) => {
    if (target.value.length >= 2) {
      this.setState({
        disabled: false,
      });
    }
    this.setState({
      [target.name]: target.value,
    });
  }

  handleClick = async () => {
    this.setState({
      hidden: true,
      loading: true,
    });

    const { value } = this.state;
    const album = await albumAPI(value);
    console.log(album);
    this.setState({
      value: ''
    });
  }

  render() {
    const { disabled, value, hidden } = this.state;
    return (
      <>
        <Header />
        <form id="search-form">
          <input
            hidden={ hidden }
            id="input-search"
            name="value"
            type="text"
            placeholder="Nome do Artista"
            onChange={ this.handleInput }
            value={ value }
          />
          <button
            hidden={ hidden }
            type="button"
            id="button-search"
            disabled={ disabled }
            onClick={ this.handleClick }
          >
            Pesquisar
          </button>
        </form>
      </>
    );
  }
}

export default Search;
