import React from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import getMusics from '../services'
import Loading from './Loading';
import MusicCard from './MusicCard';

class Album extends React.Component {
  constructor() {
    super();

    this.state = {
      loading: true,
      album: [],
      info: {
        title: '',
        image: '',
        name: '',
      },
      albumLength: 0,
    };

    this.musicApi = this.musicApi.bind(this);
  }

  componentDidMount() {
    this.musicApi();
  }

  musicApi = async () => {
    const { match: { params: { id } } } = this.props;
    const music = await getMusics(id).then((res) => res);
    const collection = [];
    const track = [];
    music.filter((e) => {
      if (e.kind !== 'song') {
        return collection.push(e);
      }
      return track.push(e);
    });

    const { artworkUrl100, artistName, collectionName } = collection[0];

    this.setState({
      album: [...track],
      loading: false,
      albumLength: music.length,
      info: {
        title: collectionName,
        image: artworkUrl100,
        name: artistName,
      },
    });
  }

  render() {
    const { loading, albumLength } = this.state;
    const { info: { title, image, name } } = this.state;
    const { album } = this.state;

    let render = null;
    if (albumLength > 0) {
      render = (
        <div id="album-container">
          <div id="albumId" data-testid="page-album">
            <img src={ image } alt={ name } />
            <h2 data-testid="album-name">{ title }</h2>
            <p data-testid="artist-name">{ name }</p>
          </div>
          <div className="sound">
            {album.map((e) => (
              <MusicCard
                key={ e.trackName }
                trackId={ e.trackId }
                trackName={ e.trackName }
                previewUrl={ e.previewUrl }
              />
            ))}
          </div>
        </div>
      );
    }

    return (
      <div>
        <Header />
        {loading ? <Loading /> : null }
        {albumLength > 0 ? render : null}
      </div>
    );
  }
}

Album.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.objectOf(PropTypes.number),
  }),
}.isRequired;

export default Album;
