import PropTypes from 'prop-types';
import React from 'react';
import heart1 from '../heart1.png';

class MusicCard extends React.Component {
  constructor() {
    super();

    this.state = {
      favorite: false,
    };
  }

  handleClick = ({ target }) => {
    console.log(target);
    if (target.checked === true) {
      if ('favorite' in localStorage) {
        const newId = { id: target.id };
        const previousRanking = JSON.parse(localStorage.getItem('ranking'));
      } else {
        localStorage.setItem('favorite', target.id);
      }
    }
  }

  render() {
    const { trackName, trackId, previewUrl } = this.props;
    const { favorite } = this.state;
    return (
      <div>
        <p className="line" />
        <div className="album-list">
          <div className="name-music">
            <p>{ trackName }</p>
          </div>
          <audio data-testid="audio-component" src={ previewUrl } controls>
            <track kind="captions" />
