            <code>audio</code>
          </audio>
          <label
            htmlFor={ trackId }
            id="teste2"
          >
            <input
              checked={ favorite }
              type="checkbox"
              id={ trackId }
              onChange={ this.handleClick }
            />
            <img src={ heart1 } id="img-favorite" alt="favorite" />
          </label>

        </div>
      </div>
    );
  }
}

MusicCard.propTypes = {
  trackName: PropTypes.string.isRequired,
