import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import albumAPI from '../services/albumAPI';
import Loading from '../components/Loading';

class Search extends React.Component {
  constructor() {
    super();

    this.state = {
      sucess: true,
      loading: false,
      hidden: false,
      disabled: true,
      artistname: true,
      artist: '',
      value: '',
      album: [],
    };
  }

  handleInput = ({ target }) => {
    if (target.value.length >= 2) {
      this.setState({
        disabled: false,
      });
    }
    this.setState({
      [target.name]: target.value,
    });
  }

  handleClick = async () => {
    this.setState({
      hidden: true,
      loading: true,
    });

    const { value } = this.state;
    const album = await albumAPI(value);
    this.setState({
      album,
      artistname: false,
      artist: value,
      loading: false,
      value: '',
    });
    if (album.length === 0) this.setState({ sucess: false });
  }

  render() {
    const {
      disabled,
      value,
      hidden,
      loading,
      artist,
      artistname,
      sucess,
      album,
    } = this.state;
    return (
      <>
        <Header />
        {loading ? <Loading />
          : (
            <form id="search-form">
              <input
                hidden={ hidden }
                id="input-search"
                name="value"
                type="text"
                placeholder="Nome do Artista"
                onChange={ this.handleInput }
                value={ value }
              />
              <button
                hidden={ hidden }
                type="button"
                id="button-search"
                disabled={ disabled }
                onClick={ this.handleClick }
              >
                Pesquisar
              </button>
            </form>
          )}
        {sucess
          ? (
            <p hidden={ artistname }>
              Resultado de Ã¡lbuns de:
              {' '}
              { artist }
            </p>
          ) : (
            <p>Nenhum Ã¡lbum foi encontrado</p>
          )}
        {sucess
          ? (
            album.map((e) => (
              <div className="div-album" key={ album.collectionId }>
                <Link
                  to={ `/album/${album.collectionId}` }
                  data-testid={ `link-to-album-${album.collectionId}` }
                >
                  <img src={ album.artworkUrl100 } alt={ album.collectionName } />
                </Link>
                <h2>{ album.collectionName }</h2>
                <p>{ album.artistName }</p>
            </div>
            ))
          ) : null}
      </>
    );
  }
}

export default Search;
